import 'package:flutter/material.dart';

void main(){
  runApp(MaterialApp(home:Refactoring() ,));
}

class Refactoring extends StatelessWidget {
  const Refactoring({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text("Refactoring 3"),
      ),
      body: GridView.builder(
        gridDelegate:
         SliverGridDelegateWithFixedCrossAxisCount(crossAxisCount: 2), itemBuilder: (context,index){
          return MyRefractoringWidget(
            rimage:Image.network("data:image/jpeg;base64,"),
            label: Text("Porsche",style: TextStyle(color: Colors.black),),
          );
         }),
    );
  }
}
 class MyRefractoringWidget extends StatelessWidget {
  final Color? bgcolor;
  final Image? rimage;
  VoidCallback? onClick;
  Widget? label;
  Widget? ricon;

  MyRefractoringWidget({
    this.bgcolor,
    required this.rimage,
    this.label,
    this.onClick,
    this.ricon,
  });

  @override
  Widget build(BuildContext context) {
    return Card(
      color: bgcolor,
      child: Center(
        child: ListTile(
          title: label,
          leading:rimage,
          onTap: onClick,
        ),
      ),
    );
  }
}